example.code:
// Settings.cs
public int SettingsVersion = 0;

public override void Save(UnityModManager.ModEntry modEntry)
{
    if (SettingsVersion == 0) SettingsVersion = 1;
    Save(this, modEntry);
}

// Main.cs Load method
settings = Settings.Load<Settings>(modEntry);
if (settings.SettingsVersion == 0)
{
    var settingsPath = Path.Combine(modEntry.Path, "Settings.xml");
    settings = SettingsRecovery.TryRecoverSettings<Settings>(settingsPath, settings);
}
if (settings.SettingsVersion == 0) settings.SettingsVersion = 1;

public class Settings : XmlModSettings
{
    public override int SettingsVersion { get; set; } = 1;
    public bool MyOption = true;
    public int MyValue = 42;
}

// In your mod's Load method:
settings = XmlModSettings.Load<Settings>(modEntry);

public class Settings : JsonModSettings
{
    protected override int CurrentVersion => 2;
    protected override string FileName => "MyModSettings.json";

    public bool MyOption = true;
    public string NewField = "default";

    protected override void MigrateJson(JObject json, int fromVersion)
    {
        if (fromVersion < 2)
        {
            json["NewField"] = "migrated_value";
        }
    }
}

// In your mod's Load method:
settings = JsonModSettings.Load<Settings>(modEntry);

